mapper.select:
select * from setmeal


        and name like concat('%',#{name},'%')


        and category_id = #{categoryId}


        and status = #{status}

select sd.name ,sd.copies,d.image,d.description
from setmeal_dish sd left join dish d on sd.dish_id=d.id
where sd.setmeal_id = #{setmealId}
